def audio_record(audio_file, RECORD_SECONDS = 60, recording_noisegate_sensitivity=4):
    import pyaudio
    import wave
    import audioop

    CHUNK = 1024
    FORMAT = pyaudio.paInt16
    CHANNELS = 1
    RATE = 44100
    WAVE_OUTPUT_FILENAME = audio_file
    noise_gate_map = {1:80, 2:100, 3:150, 4:400}
    noise_reduction_threshold = noise_gate_map[recording_noisegate_sensitivity]
    print(RECORD_SECONDS)

    p = pyaudio.PyAudio()

    stream = p.open(format=FORMAT,
                    channels=CHANNELS,
                    rate=RATE,
                    input=True,
                    frames_per_buffer=CHUNK)

    print("* recording")

    frames = []

    for i in range(0, int(RATE / CHUNK * RECORD_SECONDS)):
        data = stream.read(CHUNK)
        rms = audioop.rms(data, 2)
        print(rms)
        if rms > noise_reduction_threshold:  # noise reduction
            frames.append(data)
        else:
            frames.append(zero)

    print("* done recording")

    stream.stop_stream()
    stream.close()
    p.terminate()

    wf = wave.open(WAVE_OUTPUT_FILENAME, 'wb')
    wf.setnchannels(CHANNELS)
    wf.setsampwidth(p.get_sample_size(FORMAT))
    wf.setframerate(RATE)
    wf.writeframes(b''.join(frames))
    wf.close()

if __name__=='__main__':
    import datetime
    t = datetime.datetime.now().strftime("%y%m%d_%H%M%S")
    audio_record('flute_{0}.wav'.format(t))
